import random


def find_coprime(n):
    """Находит взаимно простое число с n."""

    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    coprime = n  # Инициализируем coprime числом, большим n
    while gcd(n, coprime) != 1:
        coprime -= random.randint(1, 100)
    return coprime

